            alright = false;

        if(fragment1.getConfpassField().getText().toString().equals(fragment1.getConfpassField().getText().toString()) == false)
            alright = false;


        //Check name
        if(fragment2.getNameField().getText().toString().length() < 3)
            alright = false;


        //Check surname
        if(fragment2.getSurnameField().getText().toString().length() < 3)
            alright = false;


        //Check date
