    /**
     *  Create a new createAccount method which takes in an email address and password,
     *  validates them and then creates a new user
     *
     *  @param email of the user who has registered
     *  @param password of the user who has registered
     */
    private void createFirebaseUser(String email, String password) {

        mAuth.createUserWithEmailAndPassword(email, password)
                .addOnCompleteListener(this, new OnCompleteListener<AuthResult>() {
                    @Override
                    public void onComplete(@NonNull Task<AuthResult> task) {
                        if (task.isSuccessful()) {
                            showDialog(true);

                            // It call the method for send the verification email
                            sendVerificationEmail();


                            //TODO: Mettere un cazzo di timer perchè l'animazione non si vede più tutta

                            //Go to LoginActivity
                            Intent go_login = new Intent(getApplicationContext(), LoginActivity.class);
                            startActivity(go_login);


                        } else {
                            // If sign in fails, display a message to the user.
                            showDialog(false);
                        }
                    }
                });
    }



    /**
     *  Shows an error message if the registration has not occurred,
     *  otherwise shows an message confirming
     *  @param result
     */
    private void showDialog(Boolean result) {

        if(!result) {
            runOnUiThread(new Runnable()
            {
                @Override
                public void run()
                {
                    // Show error message
                    new KAlertDialog(RegistrationActivity.this, KAlertDialog.ERROR_TYPE)
                            .setTitleText(getResources().getString(R.string.registrationDialogErrorText1))
                            .setContentText(getResources().getString(R.string.registrationDialogErrorText2))
                            .setConfirmText("OK")
                            .show();
                }
            });
        }else {
            runOnUiThread(new Runnable()
            {
                @Override
                public void run()
                {
                    // Show message confirming
                    new KAlertDialog(RegistrationActivity.this, KAlertDialog.SUCCESS_TYPE)
                            .setTitleText(getResources().getString(R.string.registrationDialogSuccesText1))
                            .setConfirmText("OK")
                            .show();
                }
            });
        }
    }

    /**
     *     Send a registration confirmation email
     */
    public void sendVerificationEmail() {
        FirebaseUser user = FirebaseAuth.getInstance().getCurrentUser();

        if (user != null) {
            user.sendEmailVerification()
                    .addOnCompleteListener(new OnCompleteListener<Void>() {
                        @Override
                        public void onComplete(@NonNull Task<Void> task) {
                            if (task.isSuccessful()) {
                                //Toast.makeText(SignUpActivity.this, "Signup successful. Verification email sent", Toast.LENGTH_SHORT).show();
                            }
                        }
                    });
        }

    }


